mapper.select:
select id, 
from ss_address where true

	and city_id = #{cityId}


	and area_id = #{areaId}


	and `address` like "%"#{address}"%"


	and user_id = #{userId}


	and status = #{status}


	order by ${sort}

select count(1)
from ss_address where true

	and city_id = #{cityId}


	and area_id = #{areaId}


	and `address` like "%"#{address}"%"


	and user_id = #{userId}


	and status = #{status}


	and status = #{status}

select id, 
from ss_address
where status = 1 and id = #{id}

mapper.update:
update ss_address set
city_id = #{cityId},
area_id = #{areaId},
address = #{address},
status = #{status}
where id = #{id}
