mapper.select:
select a.id, , b.`name` as contactName
from ss_address a left join ss_user b on a.user_id = b.id
where a.status = 1

	and a.city_id = #{cityId}


	and a.area_id = #{areaId}


	and a.`address` like "%"#{address}"%"


	and a.user_id = #{userId}


	and a.status = #{status}


	and b.`name` like "%"#{contactName}"%"


	order by ${sort}

select count(1)
from ss_address a left join ss_user b on a.user_id = b.id
where a.status = 1

	and a.city_id = #{cityId}


	and a.area_id = #{areaId}


	and a.`address` like "%"#{address}"%"


	and a.user_id = #{userId}


	and a.status = #{status}


	and b.`name` like "%"#{contactName}"%"

select a.id, , b.name as contactName
from ss_address a left join ss_user b on a.user_id = b.id
where a.status = 1 and a.id = #{id}

mapper.update:
update ss_address set
city_id = #{cityId},
area_id = #{areaId},
address = #{address},
status = #{status}
where id = #{id}
