mapper.select:
select id, 
from ss_area where true

	and `name` like "%"#{name}"%"


	and name_en like "%"#{nameEN}"%"


	and city_id = #{cityId}


	and status = #{status}


	order by ${sort}

select count(1)
from ss_area where true

	and `name` like "%"#{name}"%"


	and name_en like "%"#{nameEN}"%"


	and city_id = #{cityId}


	and status = #{status}

select id, 
from ss_area
where status = 1 and id = #{id}

mapper.update:
update ss_area set
`name` = #{name},
name_en = #{nameEN},
city_id = #{cityId},
status = #{status}
where id = #{id}
