mapper.select:
select id, 
from ss_box_record
where status = 1 and id = #{id}

select id, 
from ss_box_record where status in(1,2)

	and user_id = #{userId}


	and service_id = #{serviceId}


	and `type` = #{type}


	and status = #{status}


	order by ${sort}

select count(1)
from ss_box_record where status in(1,2)

	and user_id = #{userId}


	and service_id = #{serviceId}


	and `type` = #{type}


	and status = #{status}
